    struct City {
        string name;
        uint256 population;
        uint256 numberOfIndustries;
        string attorneyName;
        string attorneySurname;
        string attorneyEmail;
        uint256 telephone;
        string physicalAddress;
    }

    struct Citizen {
        string name;
        uint256 checkedTimestamp;
        string surname;
        string email;
        string dateOfBirth;
        uint256 telephone;
        string physicalAddress;
        uint256 id; //used for bip44 wallet
    }
    struct Industry {
        string name;
        uint256 registrationTimestamp;
    }
}
