// SPDX-License-Identifier: MIT

pragma solidity ^0.8.7;

import {Chainlink, ChainlinkClient} from "@chainlink/contracts/src/v0.8/ChainlinkClient.sol";
import {ConfirmedOwner} from "@chainlink/contracts/src/v0.8/shared/access/ConfirmedOwner.sol";
import {LinkTokenInterface} from "@chainlink/contracts/src/v0.8/shared/interfaces/LinkTokenInterface.sol";

contract ChainlinkTools is ChainlinkClient, ConfirmedOwner {
    using Chainlink for Chainlink.Request;
    uint256 public volume;
    bytes32 private jobId;
    uint256 private fee;

    event RequestPopulation(bytes32 indexed requestId, uint256 volume);

    constructor() ConfirmedOwner(msg.sender) {
        _setChainlinkToken(0x779877A7B0D9E8603169DdbD7836e478b4624789); //sepolia testnet
        _setChainlinkOracle(0x6090149792dAAeE9D1D568c9f9a6F6B46AA29eFD);
        jobId = "ca98366cc7314957b8c012c72f05aeeb";
        fee = (1 * LINK_DIVISIBILITY) / 10; // 0,1 * 10**18 (Varies by network and job)
    }

    function RequestPopulationData(
        string memory apiUrl //l'iso definito in nextjs
    ) public returns (bytes32 requestId) {
        Chainlink.Request memory req = _buildChainlinkRequest(
            jobId,
            address(this),
            this.fulfill.selector
        );

        // Set the URL to perform the GET request on
        req._add("get", apiUrl);

        req._add("path", "1,0,value");
        req._addInt("times", 1);
        return _sendChainlinkRequest(req, fee);
    }

    function fulfill(
        bytes32 _requestId,
        uint256 _volume
    ) public recordChainlinkFulfillment(_requestId) {
        emit RequestPopulation(_requestId, _volume);
        volume = _volume;
    }

    function balanceof() public view returns (uint256) {
        LinkTokenInterface link = LinkTokenInterface(_chainlinkTokenAddress());
        return link.balanceOf(msg.sender);
    }

    function balanceofContract() public view returns (uint256) {
        LinkTokenInterface link = LinkTokenInterface(_chainlinkTokenAddress());
        return link.balanceOf(address(this));
    }

    function fundContract() public {
        LinkTokenInterface link = LinkTokenInterface(_chainlinkTokenAddress());
        link.transfer(address(this), 15 * 10 ** 18);
    }

    function withdrawLink() public onlyOwner {
        LinkTokenInterface link = LinkTokenInterface(_chainlinkTokenAddress());
        require(
            link.transfer(msg.sender, link.balanceOf(address(this))),
            "Unable to transfer"
        );
    }
}

