pragma solidity ^0.8.7;
import "./PollutionToken.sol";
import "./Classes.sol";
import "./ChainlinkTools.sol";

contract DSESCenter {
    address private immutable admin;
    PollutionToken private immutable pt;
    ChainlinkTools public immutable ct; //rimetti private
    uint256 private constant INITIAL_SUPPLY = 10000;
    mapping(address => classes.State) public states; //for login purposes

    constructor() {
        admin = msg.sender;
        pt = new PollutionToken(INITIAL_SUPPLY, "PollutionToken", "PT");
        ct = new ChainlinkTools();
    }

    function getAdminAddress() public view returns (address) {
        return admin;
    }

    function getContractAddress() public view returns (address) {
        return address(ct);
    }

    function addState(
        string memory name,
        string memory iso,
        address stateAddr,
        string memory apiUrl
    ) public {
        //only callable by admin
        /*
        require(
            (msg.sender == admin) && (!checkExistingState(stateAddr)),
            "Error while adding a new state. Maybe you are not the owner or the state already exist"
        );*/
        uint256 decimals = 18;
        states[stateAddr] = classes.State(name, iso);
        ct.RequestPopulationData(apiUrl);
        uint256 tokenAssigned = (ct.volume() / 10 ** 6) *
            10 **
                decimals; /*0.0001% of the total of the population will be converted in token*/
        pt.approve(stateAddr, tokenAssigned); //modificable
    }

    function checkExistingState(address stateAddr) public view returns (bool) {
        return bytes(states[stateAddr].iso).length > 0; //mapping are initialized as 0.
    }
}

