    //for login purposes

    mapping(address => classes.Citizen) private citizens;
    mapping(address => classes.Industry) private industries;
    uint256 private constant multiplier = 10 ** 18; //18 are the decimals
    IPollutionToken private pt;
    address private admin;
    IDSESCenter private dsesCenter;
    IPollutionNft private pnft;
    event NoTokenCitizen(address indexed citizenAddr);

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(
        IPollutionToken ptContractAddress,
        IDSESCenter dsesCenterAddress,
        IPollutionNft pnftAddress
    ) public initializer {
        admin = msg.sender;
        pt = ptContractAddress;
        pnft = pnftAddress;
        dsesCenter = dsesCenterAddress;
        pnft.storeContractAddress(address(this), msg.sender);
        pt.storeContractAddress(address(this), msg.sender);
    }

    function updateContractAddress(address newAddr) public onlyAdmin {
        //used when redeployed only PollutionToken contract
        pt = IPollutionToken(newAddr);
        pt.storeContractAddress(address(this), msg.sender);
    }

    function getContractAddress() public view returns (address) {
        return address(pt);
    }

    function getThisAddress() public view returns (address) {
        return address(this);
    }

    function getAdminAddress() public view returns (address) {
        return admin;
    }

    function addCitizen(
        string memory name,
        address citizenAddr,
        uint256 checkedTimestamp,
        string memory surname,
        string memory email,
        string memory dateOfBirth,
        uint256 telephone,
        string memory physicalAddress
    ) public {
        require(
            dsesCenter.checkExistingCity(msg.sender) &&
                !checkExistingCitizen(citizenAddr),
            "You are not a city or citizen already exist"
        );

        //uint256 tokensAssigned = tokenToBeAssigned(); //this is omitted for semplicity and testing and it is used a constant value
        //uint256 tokensAssigned = 30 * multiplier;
        citizens[citizenAddr] = classes.Citizen(
            name,
            checkedTimestamp,
            surname,
            email,
            dateOfBirth,
            telephone,
            physicalAddress
        );
        pt.transferForCities(msg.sender, citizenAddr, tokenToBeAssigned()); //
    }

    function tokenToBeAssigned() public view returns (uint256) {
        /*
        classes.City memory city = dsesCenter.getCityByAddr(msg.sender);
        return
            (pt.getBalanceOf(msg.sender) / //balanceOfCity/(cityPopulation+20*cityNumberOfIndustries). 20 is an arbitrary number, just to allocate more tokens to industries
                (city.population + 20 * city.numberOfIndustries)) * multiplier; //getCityByAddr used because A struct is an example of a dynamically sized type that can only be passed around internally
    */
        return 30 * multiplier;
    }

    function consumePTFromCitizen(uint256 amountUsed, address sender) public {
        checkForExpiredToken(sender);
        if (pt.getBalanceOf(msg.sender) != 0) {
            pt.transferExtended(
                //msg.sender,
                sender,
                //pt.getPreviousSender(msg.sender),
                pt.getPreviousSender(sender),
                amountUsed * multiplier //can be also used a formula to calculate the amount of token to transfer
            );
        } else {
            //emit NoTokenCitizen(msg.sender);
            emit NoTokenCitizen(sender);
        }
    }

    function returnBalanceOfCitizen() public view returns (uint256) {
        return pt.getBalanceOf(msg.sender);
    }

    function checkForExpiredToken(address sender) private {
        if (
            (block.timestamp - citizens[sender].checkedTimestamp) >= //check if 30 days as passed
            methods.dayTimestamp(30)
        ) {
            //uint256 balanceOfCitizen = pt.getBalanceOf(msg.sender);
            uint256 balanceOfCitizen = pt.getBalanceOf(sender);
            checkForNft(balanceOfCitizen, sender);
            pt.transferExtended(
                //pt.getPreviousSender(msg.sender),
                pt.getPreviousSender(sender),
                //msg.sender,
                sender,
                tokenToBeAssigned() - balanceOfCitizen //example: 30 fixed tokens - 17 of balanceOfCitizen=13 token to be refilled to the citizen
            ); //return back to the state the tokens remained
            // citizens[msg.sender].checkedTimestamp = block.timestamp; //update citizen timestamp with the new timestamp of the actual date
            citizens[sender].checkedTimestamp = block.timestamp;
        }
    }

    function getCitizen(
        address citizenAddr
    ) public view returns (classes.Citizen memory) {
        return citizens[citizenAddr];
    }

    function checkForNft(uint256 balanceOfCitizen, address sender) public {
        //modify to private after testing
        {
            //uint256 balanceOfCitizen = pt.getBalanceOf(msg.sender);
            if (
                (balanceOfCitizen > 5 * multiplier) &&
                (balanceOfCitizen <= 10 * multiplier)
            ) //5 and 10 are random numbers, we want to check that 5<=balanceOfCitizen<=10 Pollution tokens
            {
                //pnft.mintNftCitizen(msg.sender, 0); //level 1 nft
                pnft.mintNftCitizen(sender, 0);
            } else if (
                (balanceOfCitizen > 10 * multiplier) &&
                (balanceOfCitizen <= 20 * multiplier)
            ) {
                //pnft.mintNftCitizen(msg.sender, 1);
                pnft.mintNftCitizen(sender, 1);
            }
        }
    }

    function getPreviousSender() public view returns (address) {
        return pt.getPreviousSender(msg.sender);
    }

    function returnBalanceOf() public view returns (uint256) {
        return pt.getBalanceOf(msg.sender);
    }

    //PROVA AD EREDITARE METODI PER I CHECK, USA EREDITà CONTRATTI
    function checkExistingCitizen(
        address citizenAddr
    ) public view returns (bool) {
        return bytes(citizens[citizenAddr].name).length > 0;
    }

    function deleteCitizen(address citizenAddr) public onlyCity {
        pt.transferExtended(
            citizenAddr,
            msg.sender,
            pt.getBalanceOf(citizenAddr)
        );
        delete citizens[citizenAddr];
    }

    function returncheckedTimestamp() public view returns (bool) {
        return
            block.timestamp - citizens[msg.sender].checkedTimestamp >
            methods.dayTimestamp(30);
    }

    function returnBlockTimestamp() public view returns (uint256) {
        return block.timestamp;
    }

    function returnNewTimestamp() public view returns (uint256) {
        return citizens[msg.sender].checkedTimestamp;
    }

    modifier onlyCity() {
        require(
            dsesCenter.checkExistingCity(msg.sender),
            "You are not allowed to do that. You are not a city"
        );
        _;
    }
    modifier onlyAdmin() {
        require(
            (msg.sender == admin),
            "You are not the owner of this contract"
        );
        _;
    }

    modifier checkMonthHasPassed() {
        if (
            (block.timestamp - citizens[msg.sender].checkedTimestamp) >=
            methods.dayTimestamp(30)
        ) _;
    }
}

