// SPDX-License-Identifier: MIT

pragma solidity ^0.8.7;

import "./Classes.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {methods} from "./Library.sol";
import "./interfaces/IDSESCenter.sol";
import "./interfaces/IPollutionToken.sol";
import "./interfaces/IPollutionNft.sol";

contract CityIndustry is Initializable {
    //for login purposes
    mapping(address => classes.Industry) private industries;
    uint256 private constant multiplier = 10 ** 18; //18 are the decimals
    IPollutionToken private pt;
    address private admin;
    IDSESCenter private dsesCenter;
    IPollutionNft private pnft;
    event NoTokenIndustry(address indexed industryAddr);

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(
        IPollutionToken ptContractAddress,
        IDSESCenter dsesCenterAddress,
        IPollutionNft pnftAddress
    ) public initializer {
        admin = msg.sender;
        pt = ptContractAddress;
        pnft = pnftAddress;
        dsesCenter = dsesCenterAddress;
        pnft.storeContractAddress(address(this), msg.sender);
        pt.storeContractAddress(address(this), msg.sender);
    }

    function getContractAddress() public view returns (address) {
        return address(pt);
    }

    function getThisAddress() public view returns (address) {
        return address(this);
    }

    function getAdminAddress() public view returns (address) {
        return admin;
    }

    function consumePTFromIndustry(
        //used by industries and Industry
        uint256 amountUsed
    ) public {
        if (pt.getBalanceOf(msg.sender) != 0) {
            pt.transferExtended(
                msg.sender,
                pt.getPreviousSender(msg.sender),
                amountUsed //can be also used a formula to calculate the amount of token to transfer
            );
        } else {
            emit NoTokenIndustry(msg.sender);
        }
    }

    function addIndustry(
        string memory name,
        address industryAddr,
        uint256 registrationTimestamp
    ) public {
        require(
            dsesCenter.checkExistingCity(msg.sender) &&
                !checkExistingIndustry(industryAddr),
            "You are not a city or industry already exist"
        );

        industries[industryAddr] = classes.Industry(
            name,
            registrationTimestamp
        );
        // uint256 tokensAssigned = tokensToBeAssigned(); //this is omitted for semplicity and testing and it is used a constant value
        uint256 tokensAssigned = 50 * multiplier;
        pt.transferExtended(msg.sender, industryAddr, tokensAssigned);
    }

    function tokensToBeAssigned() public view returns (uint256) {
        classes.City memory city = dsesCenter.getCityByAddr(msg.sender);
        return
            ((20 * pt.getBalanceOf(msg.sender)) / //balanceOfCity/(cityPopulation+20*cityNumberOfIndustries). 20 is an arbitrary number, just to allocate more tokens to industries
                (city.population + 20 * city.numberOfIndustries)) * multiplier; //getCityByAddr used because A struct is an example of a dynamically sized type that can only be passed around internally
    }

    function checkForNft() public {
        if (
            (block.timestamp - industries[msg.sender].registrationTimestamp) >=
            methods.dayTimestamp(30)
        ) {
            uint256 balanceOfIndustry = pt.getBalanceOf(msg.sender);
            if (
                balanceOfIndustry >= 5 * multiplier &&
                balanceOfIndustry <= 10 * multiplier
            ) //5 and 10 are random numbers, we want to check that 5<=balanceOfIndustry<=10 Pollution tokens
            {
                pnft.mintNftIndustry(msg.sender, 1); //level 1 nft
            } else if (
                balanceOfIndustry >= 11 * multiplier &&
                balanceOfIndustry <= 30 * multiplier
            ) {
                pnft.mintNftIndustry(msg.sender, 2); //level 2 nft
            }
        }
    }

    function checkForExpiredToken() private checkMonthHasPassed {
        {
            uint256 balanceOfIndustry = pt.getBalanceOf(msg.sender);
            if (balanceOfIndustry != 0) {
                pt.transferExtended(
                    msg.sender,
                    pt.getPreviousSender(msg.sender),
                    balanceOfIndustry
                ); //return back to the state the tokens remained
            } else {
                pt.transferExtended(
                    pt.getPreviousSender(msg.sender),
                    msg.sender,
                    50 * multiplier
                    //tokenToBeAssigned() //this is omitted for semplicity and testing and it is used a constant value
                );
            }
        }
    }

    function checkExistingIndustry(
        address industryAddr
    ) public view returns (bool) {
        return bytes(industries[industryAddr].name).length > 0;
    }

    function deleteIndustry(address industryAddr) public onlyCity {
        delete industries[industryAddr];
    }

    function checkForCity() public view returns (uint256) {
        return dsesCenter.getCityByAddr(msg.sender).population;
    }

    modifier onlyCity() {
        require(
            dsesCenter.checkExistingCity(msg.sender),
            "You are not allowed to do that. You are not a City"
        );
        _;
    }

    modifier checkMonthHasPassed() {
        if (
            (block.timestamp - industries[msg.sender].registrationTimestamp) >=
            methods.dayTimestamp(30)
        ) _;
    }
}

