// SPDX-License-Identifier: MIT

pragma solidity ^0.8.7;
import "./interfaces/IPollutionToken.sol";
import "./Classes.sol";
import "./ChainlinkTools.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

interface IDSESCenter {
    function getCityByAddr(
        address cityAddr
    ) external view returns (classes.City memory);

    function checkExistingCity(address cityAddr) external view returns (bool);
}

contract DSESCenter is Initializable {
    address private admin;
    IPollutionToken private pt;
    ChainlinkTools private ct;
    uint256 private constant INITIAL_SUPPLY = 100000;
    uint256 private constant multiplier = 10 ** 18;
    mapping(address => classes.State) private states; //for login purposes
    mapping(address => classes.City) private cities;

    error DSESCenter__Only_State_Allowed();
    error DSESCenter__Only_Admin_Allowed();

    /*
    constructor(PollutionToken ptContractAddress) {
        admin = msg.sender;
        pt = ptContractAddress;
        pt.storeContractAddress(address(this), msg.sender);
        ct = new ChainlinkTools();
    }
*/
    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(IPollutionToken ptContractAddress) public initializer {
        admin = msg.sender;
        pt = ptContractAddress;
        pt.storeContractAddress(address(this), msg.sender);
        ct = new ChainlinkTools();
    }

    function checkExistingAdmin(address adminAddr) public view returns (bool) {
        return adminAddr == admin ? true : false;
    }

    function updateContractAddress(address newAddr) public onlyAdmin {
        //DEPRECATED
        //used when redeployed only PollutionToken contract
        pt = IPollutionToken(newAddr);
        pt.storeContractAddress(address(this), msg.sender);
    }

    function getCityByAddr(
        address cityAddr
    ) public view returns (classes.City memory) {
        return cities[cityAddr];
    }

    function getContractAddress() public view returns (address) {
        return address(pt);
    }

    function getThisAddress() public view returns (address) {
        return address(this);
    }

    function addState(
        //FAI DEI TEST GAS USANDO MEMORY E CLASSE DERIVATA E SENZA CLASSE DERIVATA
        string memory name,
        string memory iso,
        address stateAddr,
        int numberOfCities,
        string memory attorneyName,
        string memory attorneySurname,
        string memory attorneyEmail,
        uint256 telephone,
        string memory physicalAddress
    ) external {
        //only callable by admin

        require(
            (msg.sender == admin) && (!checkExistingState(stateAddr)),
            "Error while adding a new state. Maybe you are not the owner or the state already exist"
        );
        states[stateAddr] = classes.State(
            name,
            iso,
            numberOfCities,
            attorneyName,
            attorneySurname,
            attorneyEmail,
            telephone,
            physicalAddress
        );

        pt.transferExtended(msg.sender, stateAddr, 30000 * multiplier); //modificable}
    }

    function deleteState(address stateAddr) external onlyAdmin {
        delete states[stateAddr];
    }

    function getState(
        address stateAddr
    ) public view returns (classes.State memory) {
        return states[stateAddr];
    }

    function getStateName(
        address stateAddr
    ) public view returns (string memory) {
        return states[stateAddr].name;
    }

    function getStateNumber(address stateAddr) public view returns (uint256) {
        return states[stateAddr].telephone;
    }

    function checkExistingState(address addr) public view returns (bool) {
        return bytes(states[addr].name).length > 0; //mapping are initialized as 0.
    }

    function transferToState(
        address stateAddrDestination,
        uint256 amount
    ) external onlyState {
        pt.transferExtended(msg.sender, stateAddrDestination, amount);
    }

    function addCity(
        string memory name,
        uint256 population,
        uint256 numberOfIndustries,
        address cityAddr,
        string memory attorneyName,
        string memory attorneySurname,
        string memory attorneyEmail,
        uint256 telephone,
        string memory physicalAddress
    ) external {
        require(
            checkExistingState(msg.sender) && !checkExistingCity(cityAddr),
            "Error while adding a new city. Maybe you are not a state or the city already exist"
        );

        cities[cityAddr] = classes.City(
            name,
            population,
            numberOfIndustries,
            attorneyName,
            attorneySurname,
            attorneyEmail,
            telephone,
            physicalAddress
        );
        /*
        uint256 tokenAssigned = (
            (pt.getBalanceOf(msg.sender) / states[msg.sender].numberOfCities)
        ) * multiplier; //#(tokenState/numberOfCitiesState)/cityPopulation
        */
        uint256 tokenAssigned = 1000 * multiplier;
        pt.transferExtended(msg.sender, cityAddr, tokenAssigned);
    }

    function checkExistingCity(address cityAddr) public view returns (bool) {
        return bytes(cities[cityAddr].name).length > 0; //mapping are initialized as 0.
    }

    function deleteCity(address cityAddr) external onlyState {
        delete cities[cityAddr];
    }

    function getCityName(address cityAddr) public view returns (string memory) {
        return cities[cityAddr].name;
    }

    function getCity(
        address cityAddr
    ) public view returns (classes.City memory) {
        return cities[cityAddr];
    }

    function getBalanceOf(address addr) public view returns (uint256) {
        return pt.getBalanceOf(addr);
    }

    function getMsgSenderAddr() public view returns (address) {
        return msg.sender;
    }

    modifier onlyAdmin() {
        if ((msg.sender != admin)) {
            revert DSESCenter__Only_Admin_Allowed();
        }
        _;
    }

    modifier onlyState() {
        if ((!checkExistingState(msg.sender))) {
            revert DSESCenter__Only_State_Allowed();
        }
        _;
    }
}

