// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
//import "@openzeppelin/contracts/access/Ownable.sol";
// The new-version of "@openzeppelin/contracts": "^5.0.1", already has Base64.sol
// You can import it like as shown just below...
import "@openzeppelin/contracts/utils/Base64.sol"; // 👈 comment in this import
import "@openzeppelin/contracts/utils/Strings.sol";
// instead of installing "base64-sol": "^1.1.0",
//import "base64-sol/base64.sol"; // 👈 comment out this import
import "hardhat/console.sol";

error ERC721Metadata__URI_QueryFor_NonExistentToken();
error PollutionNft__Only_Admin_Allowed();

contract PollutionNft is ERC721 {
    uint256 private s_tokenCounter;
    mapping(string => string) private industrySvgMap;
    mapping(string => string) private citizenSvgMap;
    mapping(address => address) private contractAddresses;
    address private admin;

    string[2] private industryBaseSvg;
    string[2] private citizenBaseSvg;
    mapping(uint256 => string) private tIDtoImageURI;

    mapping(uint256 => int256) private s_tokenIdToHighValues;
    event CreatedNFT(uint256 indexed tokenId, string typeOfNft, uint256 level);

    constructor(
        string[2] memory industrySvg,
        string[2] memory citizenSvg //format:<svg width=\"320\" height=\"130\" xmlns=\"http://www.w3.org/2000/svg\"><rect width=\"300\" height=\"100\" x=\"10\" y=\"10\" style=\"fill:rgb(0,0,255);stroke-width:3;stroke:red\" /><text x=\"50\" y=\"50\" font-family=\"Arial\" font-size=\"20\" fill=\"black\">
    ) ERC721("Pollution NFT", "PNFT") {
        //the rest of the svg will be added lately, like the text that will be the address of the "minter"
        s_tokenCounter = 0;
        admin = msg.sender;
        for (uint256 i = 0; i < industrySvg.length; i++) {
            industryBaseSvg[i] = industrySvg[i];
        }

        for (uint256 i = 0; i < citizenSvg.length; i++) {
            citizenBaseSvg[i] = citizenSvg[i];
        }
    }

    function getCitizenSvg(uint256 i) public view returns (string memory) {
        return citizenBaseSvg[i];
    }

    function getIndustrySvg(uint256 i) public view returns (string memory) {
        return industryBaseSvg[i];
    }

    function mintNftIndustry(address to, uint256 level) public onlyOwners {
        uint256 newTokenId = s_tokenCounter;
        tIDtoImageURI[newTokenId] = svgToImageURI(industryBaseSvg[level], to);
        //s_tokenIdToHighValues[newTokenId] = level;
        _safeMint(to, newTokenId);
        s_tokenCounter = s_tokenCounter + 1;
        emit CreatedNFT(newTokenId, "industry", level);
    }

    function mintNftCitizen(address to, uint256 level) public onlyOwners {
        uint256 newTokenId = s_tokenCounter;

        //s_tokenIdToHighValues[newTokenId] = to;
        tIDtoImageURI[newTokenId] = svgToImageURI(citizenBaseSvg[level], to);
        _safeMint(to, newTokenId);
        s_tokenCounter = s_tokenCounter + 1;
        emit CreatedNFT(newTokenId, "citizen", level);
    }

    // You could also just upload the raw SVG and let solildity convert it!
    function svgToImageURI(
        string memory svg,
        address to
    ) public pure returns (string memory) {
        // example:
        // '<svg width="500" height="500" viewBox="0 0 285 350" fill="none" xmlns="http://www.w3.org/2000/svg"><path fill="black" d="M150,0,L75,200,L225,200,Z"></path></svg>'
        // would return ""

        string memory endOfSvg = "</text></svg>";
        string memory baseURL = "data:image/svg+xml;base64,";
        string memory svgBase64Encoded = Base64.encode(
            bytes(
                string(abi.encodePacked(svg, Strings.toHexString(to), endOfSvg))
            ) //CONTROLLA VIDEO SE è CORRETYTO COSì
        );
        return string(abi.encodePacked(baseURL, svgBase64Encoded));
    }

    function _baseURI() internal pure override returns (string memory) {
        return "data:application/json;base64,";
    }

    function tokenURI(
        uint256 tokenId
    ) public view virtual override returns (string memory) {
        if (_ownerOf(tokenId) == address(0)) {
            revert ERC721Metadata__URI_QueryFor_NonExistentToken();
        }
        string memory imageURI = tIDtoImageURI[tokenId];
        return
            string(
                abi.encodePacked(
                    _baseURI(),
                    Base64.encode(
                        bytes(
                            abi.encodePacked(
                                '{"name":"',
                                name(), // You can add whatever name here
                                '" original owner address:"',
                                Strings.toHexString(_ownerOf(tokenId)),
                                '", "description":"An NFT for the DSES application", ',
                                '"image":"',
                                imageURI,
                                '"}'
                            )
                        )
                    )
                )
            );
    }

    function storeContractAddress(address contractAddr, address sender) public {
        //sender will be msg.sender that has to be the admin
        require(
            sender == admin,
            "you are not allowed to do that, you are not the admin"
        );
        contractAddresses[contractAddr] = sender;
    }

    function getTokenCounter() public view returns (uint256) {
        return s_tokenCounter;
    }

    modifier onlyOwners() {
        if (contractAddresses[msg.sender] != admin) {
            revert PollutionNft__Only_Admin_Allowed();
        }

        _;
    }
}

contracts/PollutionToken.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.7;
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

interface tokenRecipient {
    function receiveApproval(
        address _from,
        uint256 _value,
        address _token,
        bytes calldata _extraData
    ) external;
}

error PollutionToken__Only_Allowed_Contracts();

contract PollutionToken is Initializable {
    // Public variables of the token
    string public name;
    string public symbol;
    uint8 public decimals;
    // 18 decimals is the strongly suggested default, avoid changing it
    uint256 public totalSupply;
    address private admin;
    mapping(address => address) contractAddresses; //used to store contract addresses allowed by admin that interact with this contract

    // This creates an array with all balances
    mapping(address => uint256) public balanceOf;
    mapping(address => mapping(address => uint256)) public allowance;
    mapping(address => address) public reversePartecipants; //used for the transfer from citizens/industry back to city and from city to state

    // This generates a public event on the blockchain that will notify clients
    event Transfer(address indexed from, address indexed to, uint256 value);

    // This generates a public event on the blockchain that will notify clients
    event Approval(
        address indexed _owner,
        address indexed _spender,
        uint256 _value
    );

    // This notifies clients about the amount burnt
    event Burn(address indexed from, uint256 value);

    /**
     * Constructor function
     *
     * Initializes contract with initial supply tokens to the creator of the contract
     */

    /*
    constructor(
        uint256 initialSupply,
        string memory tokenName,
        string memory tokenSymbol
    ) {
        totalSupply = initialSupply * 10 ** uint256(decimals); // Update total supply with the decimal amount
        balanceOf[msg.sender] = totalSupply; // Give the creator all initial tokens
        admin = msg.sender;
        name = tokenName; // Set the name for display purposes
        symbol = tokenSymbol; // Set the symbol for display purposes
    }*/
    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(
        uint256 initialSupply,
        string memory tokenName,
        string memory tokenSymbol
    ) public initializer {
        decimals = 18;
        totalSupply = initialSupply * 10 ** uint256(decimals); // Update total supply with the decimal amount
        balanceOf[msg.sender] = totalSupply; // Give the creator all initial tokens
        admin = msg.sender;
        name = tokenName; // Set the name for display purposes
        symbol = tokenSymbol; // Set the symbol for display purposes
    }

    function getAdminAddress() public view returns (address) {
        return admin;
    }

    function getBalanceOf(address addr) public view returns (uint256) {
        return balanceOf[addr];
    }

    /**
     * Internal transfer, only can be called by this contract
     */
    function _transfer(address _from, address _to, uint256 _value) internal {
        // Prevent transfer to 0x0 address. Use burn() instead
        require(_to != address(0x0));
        // Check if the sender has enough
        require(balanceOf[_from] >= _value);
        // Check for overflows
        require(balanceOf[_to] + _value >= balanceOf[_to]);
        // Save this for an assertion in the future
        uint256 previousBalances = balanceOf[_from] + balanceOf[_to];
        // Subtract from the sender
        balanceOf[_from] -= _value;
        // Add the same to the recipient
        balanceOf[_to] += _value;
        emit Transfer(_from, _to, _value);
        // Asserts are used to use static analysis to find bugs in your code. They should never fail
        assert(balanceOf[_from] + balanceOf[_to] == previousBalances);
    }

    /**
     * Transfer tokens
     *
     * Send `_value` tokens to `_to` from your account
     *
     * @param _to The address of the recipient
     * @param _value the amount to send
     */
    function transfer(
        address _to,
        uint256 _value
    ) public returns (bool success) {
        _transfer(msg.sender, _to, _value);
        return true;
    }

    function transferExtended(
        address from,
        address to,
        uint256 value
    ) public onlyAllowedContracts {
        _transfer(from, to, value);
    }

    function transferForCities(
        address from,
        address to,
        uint256 value
    ) public onlyAllowedContracts {
        reversePartecipants[to] = from;
        transferExtended(from, to, value);
    }

    /**
     * Transfer tokens from other address
     *
     * Send `_value` tokens to `_to` on behalf of `_from`
     *
     * @param _from The address of the sender
     * @param _to The address of the recipient
     * @param _value the amount to send
     */
    function transferFrom(
        address _from,
        address _to,
        uint256 _value
    ) public returns (bool success) {
        //require(_value <= allowance[_from][msg.sender]); // Check allowance
        require(
            (_value <= allowance[_from][_to]) &&
                contractAddresses[msg.sender] == admin,
            "you are not allowed to do transferFrom function"
        );
        //allowance[_from][msg.sender] -= _value;
        allowance[_from][_to] -= _value;
        _transfer(_from, _to, _value);
        return true;
    }

    function getPreviousSender(address from) public view returns (address) {
        return reversePartecipants[from];
    }

    /**
     * Set allowance for other address
     *
     * Allows `_spender` to spend no more than `_value` tokens on your behalf
     *
     * @param _spender The address authorized to spend
     * @param _value the max amount they can spend
     */
    function approve(
        address _spender,
        uint256 _value
    ) public returns (bool success) {
        //allowance[msg.sender][_spender] = _value;
        require(contractAddresses[msg.sender] == admin, "you are not approved");
        allowance[msg.sender][_spender] = _value;
        emit Approval(msg.sender, _spender, _value);
        return true;
    }

    function storeContractAddress(address contractAddr, address sender) public {
        //sender will be msg.sender that has to be the admin
        require(
            sender == admin,
            "you are not allowed to do that, you are not the admin"
        );
        contractAddresses[contractAddr] = sender;
    }

    function getContractAddress() public view returns (address) {
        return msg.sender;
    }

    function approveExtended(
        address giver,
        address _spender,
        uint256 _value
    ) public returns (bool success) {
        //allowance[msg.sender][_spender] = _value;
        require(
            contractAddresses[msg.sender] == admin,
            "you are not approved by the admin"
        );
        allowance[giver][_spender] = _value;
        emit Approval(giver, _spender, _value);
        return true;
    }

    /**
     * Set allowance for other address and notify
     *
     * Allows `_spender` to spend no more than `_value` tokens on your behalf, and then ping the contract about it
     *
     * @param _spender The address authorized to spend
     * @param _value the max amount they can spend
     * @param _extraData some extra information to send to the approved contract
     */
    function approveAndCall(
        address _spender,
        uint256 _value,
        bytes memory _extraData
    ) public returns (bool success) {
        tokenRecipient spender = tokenRecipient(_spender);
        if (approve(_spender, _value)) {
            spender.receiveApproval(
                msg.sender,
                _value,
                address(this),
                _extraData
            );
            return true;
        }
    }

    /**
     * Destroy tokens
     *
     * Remove `_value` tokens from the system irreversibly
     *
     * @param _value the amount of money to burn
     */
    function burn(uint256 _value) public returns (bool success) {
        require(balanceOf[msg.sender] >= _value); // Check if the sender has enough
        balanceOf[msg.sender] -= _value; // Subtract from the sender
        totalSupply -= _value; // Updates totalSupply
        emit Burn(msg.sender, _value);
        return true;
    }

    function getAllowance(
        address master,
        address slave
    ) public view returns (uint256) {
        return allowance[master][slave];
    }

    /**
     * Destroy tokens from other account
     *
     * Remove `_value` tokens from the system irreversibly on behalf of `_from`.
     *
     * @param _from the address of the sender
     * @param _value the amount of money to burn
     */
    function burnFrom(
        address _from,
        uint256 _value
    ) public returns (bool success) {
        require(balanceOf[_from] >= _value); // Check if the targeted balance is enough
        require(_value <= allowance[_from][msg.sender]); // Check allowance
        balanceOf[_from] -= _value; // Subtract from the targeted balance
        allowance[_from][msg.sender] -= _value; // Subtract from the sender's allowance
        totalSupply -= _value; // Update totalSupply
        emit Burn(_from, _value); //updat
        return true;
    }

    modifier onlyAllowedContracts() {
        if (contractAddresses[msg.sender] != admin) {
            revert PollutionToken__Only_Allowed_Contracts();
        }
        _;
    }
}

